import React, {useState} from 'react';
//import data
import {pricing} from '../data'
//import icon
import {HiCheck, HiOutlineArrowNarrowRight} from 'react-icons/hi';

const Pricing = () => {
  //index state
  const [index, setIndex] = useState(1);
  // pricing
  const {title, cards} = pricing;
  return <section className="bg-pink-200 section">
    <div className="container mx-auto">
      {/* title */}
      <h2 className="h-2 mb-10 lg:mb-20 text-center">{title}</h2>
      {/* cards */}
      <div className="flex flex-col lg:flex-row lg:gap-x-[30px] gap-y-[30px] lg:gap-y-0 justify-center items-center">
        {cards.map((card, cardIndex)=>{
          const {icon, title, services, price, userAmount, btnText, delay} = card;
          return <div key={cardIndex}>
            <div onClick={()=> setIndex(cardIndex)} className={`${cardIndex === index ? 'bg-white shadow-2xl' : 'border' +
                ' border-gray'} w-[350px] h-[500px] rounded-[12px] p-[40px] cursor-pointer transition-all`}>
            {/*  card icon */}
              <div className="mb-8">
                <img src={icon} alt=""/>
              </div>
            {/* card title */}
              <div className="text-[32px] font-semibold mb-8">{title}</div>
            {/* card service */}
              <div className="flex flex-col gap-y-2 mb-6">
                {services.map((service, index)=>{
                  //service
                  const {name} = service;
                  return <div className="flex items-center gap-x-[10px]" key={index}>
                    <HiCheck className="text-light"/>
                    <div>
                      {name}
                    </div>
                  </div>
                })}
              </div>
              <div className="mb-10">
                <div >
                  <span className="text-2xl font-semibold">{price}</span>
                  <span className="text-xl text-light font-light">year</span>
                </div>
                <div className="text-base text-light">{userAmount}</div>
              </div>
            {/* btn */}
              <button className={`${cardIndex === index ? 'bg-accent hover:bg-accent text-white' : 'border' +
                  ' border-accent text-accent'} btn btn-sm`}>
                <span>{btnText}</span>
              </button>
            </div>
          </div>
        })}
      </div>
    </div>
  </section>;
};

export default Pricing;
