import React,{useState} from 'react';
//import data
import {product} from '../data';
//import image
import ArrowImg from '../assets/img/product/cards/arrow.svg'

const Cards = () => {
  //index state
  const [index, setIndex] = useState(1);
  //product data
  const {cards} = product;
  return <div>
  {/*  cards */}
    <div className="flex flex-col gap-y-[30px] lg:flex-row lg:gap-x-[30px]">
      {cards.map((card, cardIndex) => {
        const {title, subtitle, icon, delay} = card;
        return <div key={cardIndex}>
          <div onClick={() => setIndex(cardIndex)}
              className={`${index === cardIndex && 'bg-white shadow-2xl'} w-[350px] h-[350px] flex flex-col justify-center mx-auto p-[65px]' +
              ' text-center rounded-[12px cursor-pointer transition-all]`}>
          {/* card icon */}
            <div>
              <img src={icon} alt=""/>
            </div>
            {/* card title */}
            <div>{title}</div>
            {/* card subtitle */}
            <p>{subtitle}</p>
            {/* arrow image */}
            {index === cardIndex }
          </div>
        </div>;
      })}
    </div>
  </div>;
};

export default Cards;
